    if (alarmload == 0 )         { alarm.volume=0;          alarm.muted=true;          alarm.play();           alarm.pause();          alarmload=1;}
    if (evt.target.classList.contains('child')) {
      if (evt.target.parentNode.classList.contains('turnable') || 
          evt.target.parentNode.classList.contains('pressable') || 
          evt.target.parentNode.classList.contains('liftable')) {
        selectedElement = evt.target.parentNode;
      }
    } else if (evt.target.classList.contains('turnable') || 
          evt.target.classList.contains('pressable') || 
          evt.target.classList.contains('liftable')) {
      selectedElement = evt.target;
    }

    if (selectedElement) { // Assume only one of turnable, liftable, pressable and slidable
      if (selectedElement.classList.contains('turnable')) {
        selectedAt = getMousePosition(evt);
        bbox = selectedElement.getBBox();
        centre.x = bbox.x + (bbox.width/2);
        centre.y = bbox.y + (bbox.height/2);
        // Get state of rotate transform
        transforms = selectedElement.transform.baseVal;
        if (transforms.length === 0 || transforms.getItem(0).type !== SVGTransform.SVG_TRANSFORM_ROTATE) {
          // If there is no existing rotate at beginning of transform list then make one
          var rotate = svg.createSVGTransform();
          rotate.setRotate(0, centre.x, centre.y);
          selectedElement.transform.baseVal.insertItemBefore(rotate, 0); // place this rotate at the beginning
        }
        let startAngle=mod(((Math.atan2(selectedAt.x-centre.x,selectedAt.y-centre.y)*180/Math.PI)),360); //get angle of leaver handle
        lastAngle=startAngle; // set this to startAngle at start of rotate
      }
      else if (selectedElement.classList.contains('pressable')) { // If 'pressed' add pressed class list
        if (on) {
          if (!selectedElement.classList.contains('pressed')) {
            selectedElement.classList.add("pressed");
          }
