  function drag(evt) {
    if (selectedElement) { // can only drag if an element is selected
      evt.preventDefault(); // turn off default actions ???Needed???
      if (selectedElement.classList.contains('turnable')) {
        var draggedTo  = getMousePosition(evt); // where is the mouse now?
        var thisAngle  = mod(((Math.atan2(draggedTo.x-centre.x,draggedTo.y-centre.y)*180/Math.PI)),360); // new lever position
        let upperLim = lastAngle+180;
        let lowerLim = lastAngle-180;
        if (thisAngle > upperLim) { thisAngle -= 360; }       // This maintains best idea of rotation direction assuming
        else if (thisAngle <= lowerLim) { thisAngle += 360; } // drag event is fired more frequently than any other;

        let diffAngle = thisAngle - lastAngle;

        currentAngle   = selectedElement.transform.baseVal.getItem(0).angle; // Should select the transform we want
        rotateTo = currentAngle-diffAngle;

        if(rotateTo > 300) {rotateTo=300;}
        if(rotateTo < 0)   {rotateTo=0;}

        if      (selectedElement.id=='Volume') { 
          volumeFloat=rotateTo*11/300; volume=Math.round(volumeFloat); 
          if ( lastAngle != thisAngle ) {
            if (navigator.platform.match(/iPad/i) )                                          { document.getElementById("topnote").innerHTML="For Ipad volume use Ipad's volume controls!";}
            else if (navigator.platform.match(/MacIntel/i) && navigator.maxTouchPoints > 1 ) { document.getElementById("topnote").innerHTML="For Ipad volume use Ipad's volume controls!";}
          }
        }
        else if (selectedElement.id=='Squelch') { squelch=Math.round(rotateTo*100/300); }
        lastAngle = thisAngle;

// Do the View update
        selectedElement.transform.baseVal.getItem(0).setRotate(rotateTo,centre.x,centre.y);
        if (on||booting) {
          document.getElementById("Row1").innerHTML="volume"+" ".repeat(3-volume.toString().length)+" "+volume;
          document.getElementById("Row2").innerHTML="squelch"+" ".repeat(4-squelch.toString().length)+" "+squelch;
        }
              
        if ( volume < 1) {
          powerOff();
          pauseNoise();
          pauseMayday();
          pauseAlarm();
        }
        else {
          powerOn();
          whiteNoise.volume=volumeFloat/11;
          whiteNoiseCopy.volume=volumeFloat/11;
          if ( screen.channel!=15 ) {
            mayday.volume=0;
            mayday.muted=true;
