            resetTimedHint("Set the SQUELCH just to the point so that you cannot hear the static.<br />(Too much squelch will hide incoming radio signals!)",10000);
          } else {
            pauseNoise();
            muteNoise();
            if (typeof alarmDelay == "undefined" && playingAlarm == 0 && playingMayday == 0) { 
              alarmDelay=setTimeout(playAlarm, ((Math.random() * 3000)+2000));
            }
          }
        }
      }
    }
